    // Act
    $jwsSignature = "eyJhbGciOiJSUzI1NiJ9._.o9j6K72t56gIGW_TtIeRgxjWPmQstwCQqs2zeYMA5sQEZKGZmYaLfUaSvJEbIXUfbUlEjlc_Xtm2EKsCZCkGY5sveA-JSVJhtz1zui7ToOjaTsufIj3iIxG2v8SRvcTqoiwvCTwlBzWodn7LyQd8faAVzH4QQqzwbzZgL0zDuzbi2L6E4T5SeFYILLI7mwxFkbhzfd71hALZGAZ2Ix1iIIWcOztFwCmafB_PDytI18udT1n2qgh5FrOxywoFZhlyWnmKKQ27l8RlX8KB1AggdrT-hbrGnxwW6AcxmO9ZQYMb5l3oBM4el2WLtD83fZPMyKICkcVNVhQ4wlRYrgygLQ";
    $anotherValidJwsSignature = "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXUyJ9._.qGEfYWA3VmDeMLb55jyYU4kgFUci11GOUT05IgwnEWu_67I2tElHOLdHe29M40iTSq4y-dVLsYfPVjY_8bx0Hq1dv2E0d5_fro1c-CGhvdEhkf5D4Fy-oj10C59t9RRuf9WlLEgfSgVpXcHnekkhcyIzTfxN-pmsD6D6Q8tugTmfAjhxbTA6jf99uO1aAY0cBsvcvW9G-sdIG5fTKe7wZCtUdEDO1Fb4KMP_fWEo6i1wdeKgqKTxzknfkLnalR1a41kn6C8fiSU6H1H-QaY4oyag65LrYr1G3TDi3-O0p1WNQJ8hxGx8vsyL4yv1c_DHAqgXFuGFs5OuJSob7hcklQ";
    $specialCharsContainingJwsSignature = "eyJhbGciOiJSUzI1NiJ9.eyJhdXRob3IiOiJhbGljZUBiZmguY2giLCJlbGVjdGlvblRpdGxlIjoiXCJcIlwiXCJcIiTCqF5ewrV-.ek1kenZ9KjJyjQJCSxcN3GA6o4Kz9ChF5TSw-656ciSKXUBQQ75Q9a2S87MWykxwPeUpAQbSA__VA6_dywUemICi1dzHZqmRh5Vv3V2bU5D8tMCSk_kPLhU-isgvcPuRR5yG_hzq6W6dz7ELK9wFbiZstzqOpmkiFnQNRP8WcQ5I279NBIZ4PMzFvfkn0Y6iY3eOCS0q1ceTioeCzhi2r644DswYacMfUvNj6UZaypWgPOFlcqGd5uzQAOtzX3NL09kYHpu4HuLeOY-XL8aKMN4zowLi6oGWgBe1pp5aZWEFTfnunC0MAR8WEFEkCdI2EFN3UVGwhnmFR-kXEe3Sgw";
    $invalidJwsSignature  = "eyJhbGciOiJSUzI1NiJ9.eyJhdXRob3IiOiJhbGljZUBiZmguY2giLCJlbGVjdGlvblRpdGxlIjoiXCJcIlwiXCJcIiTCCF5ewrV-.ek1kenZ9KjJyjQJCSxcN3GA6o4Kz9ChF5TSw-656ciSKXUBQQ75Q9a2S87MWykxwPeUpAQbSA__VA6_dywUemICi1dzHZqmRh5Vv3V2bU5D8tMCSk_kPLhU-isgvcPuRR5yG_hzq6W6dz7ELK9wFbiZstzqOpmkiFnQNRP8WcQ5I279NBIZ4PMzFvfkn0Y6iY3eOCS0q1ceTioeCzhi2r644DswYacMfUvNj6UZaypWgPOFlcqGd5uzQAOtzX3NL09kYHpu4HuLeOY-XL8aKMN4zowLi6oGWgBe1pp5aZWEFTfnunC0MAR8WEFEkCdI2EFN3UVGwhnmFR-kXEe3Sgw";

    // Assert
    $this->assertTrue(verify_signature($email, $alicesCertificate, $jwsSignature));
    $this->assertTrue(verify_signature($email, $alicesCertificate, $anotherValidJwsSignature));
    $this->assertTrue(verify_signature($email, $alicesCertificate, $specialCharsContainingJwsSignature));

    $this->assertFalse(verify_signature($email, $bobsCertificate, $jwsSignature));
