      "author" => "alice@bfh.ch",
      "electionTitle" => "Another\$ä",
      "beginDate" => "2016-12-27 00:00:00",
      "endDate" => "2017-01-08 00:00:00",
      "appVersion" => "0.15",
      "coefficients" => "",
      "electionGenerator" => "",
      "votingTopic" => array(
        "topic" => "What",
