      ),
      "voters" => array(
        [
        "email" => "alice@bfh.ch",
        "signature" => "standInSignatur",
        "publicCredential" => "",
        "appVersion" => "1.15"
        ]
      )
    );

    // Act
    $temperedJwsPayload = array(
          "author" => "alice@bfh.ch",
          "electionTitle" => "Another ",
          "beginDate" => "2016-12-27 00:00:00",
          "endDate" => "2017-01-08 00:00:00",
